mapper.select:
select count(id)
from message
where from_id = 1 and to_id = #{userId} and status = 0 and conversation_id = #{conversationId}

select  
from message
where from_id = 1 and to_id = #{userId} and conversation_id = #{conversationId}
order by id desc
limit #{offset},#{limit}

mapper.update:
update message
set status  = #{status}
where to_id =#{userId} and from_id = 1

mapper.select:
select count(*)
from message
where from_id = 1 and to_id = #{userId} and conversation_id = #{conversationId}
